import { useState, useEffect } from "react";
import {ComposableMap, Geographies, Geography, ZoomableGroup } from "react-simple-maps";
import { geoCentroid } from "d3-geo"; 
import { feature } from "topojson-client";

import '../ComponentStyles/worldmap.css';
import { area } from "@turf/turf";

function WorldMap({ selectedCountries }) {
  const [countryDataJson, setCountryDataJson] = useState(null);
  const [center, setCenter] = useState([0, 0]);
  const [zoom, setZoom] = useState(1);
  const RUSSIA_AREA = 10346150.6498;

  useEffect(() => {
    async function fetchCountryData() {
      const res = await fetch("/CountriesView.json");
      const json = await res.json();
      setCountryDataJson(json);
    }

    fetchCountryData();
  }, []);

  useEffect(() => {
    const countries = countryDataJson && feature(countryDataJson, countryDataJson.objects.countries).features;
    
    if(countries) {
      const match = countries.find(x => x.properties.name.toLowerCase() === selectedCountries.name.trim().toLowerCase()); 
      
      
      if(match) {
        const countryArea = area(match) / 1000000;
  
        const rawScale = (RUSSIA_AREA * 1.5) / countryArea;
        const clampedScale = Math.max(1, Math.min(rawScale, 300));
        const zoom = Math.min(8, Math.max(3, 8 - Math.log2(clampedScale) * 0.4));
        setCenter(geoCentroid(match));
        console.log(zoom);
        setZoom(zoom);
      }
      else {
        setZoom(1);
        setCenter([0, 0]);
      }
    }
  }, [countryDataJson, selectedCountries])

  return (
    countryDataJson && (
      <ComposableMap
        projection="geoMercator"
        className="map-container"
      >
        <ZoomableGroup
          zoom={zoom}
          center={center}
          minZoom={1}
          maxZoom={8}
          translateExtent={[[-1000, -500], [1000, 500]]}
        >
          <Geographies geography={countryDataJson}>
            {({ geographies }) =>
              geographies.map((geo) => {
                const isSearched = geo.properties.name.toLowerCase() === selectedCountries.name.toLowerCase();
                return (
                    <Geography
                      key={geo.rsmKey}
                      geography={geo}
                      fill={isSearched ? "#996cfb" : "#e0e0e0"}
                      stroke="#ffffff"
                      style={{
                        default: { outline: "none" },
                        hover: { fill: "#a78bfa", outline: "none" },
                        pressed: { fill: "#7243da", outline: "none" },
                      }}
                    />
                )})
            }
          </Geographies>
        </ZoomableGroup>
      </ComposableMap>
    )
  );
}

export default WorldMap;
