import { useState, useEffect } from "react";
import {ComposableMap, Geographies, Geography, ZoomableGroup } from "react-simple-maps";
import { geoCentroid } from "d3-geo"; 
import { feature } from "topojson-client";

import '../ComponentStyles/worldmap.css';

function WorldMap() {
  const [countryDataJson, setCountryDataJson] = useState(null);
  const [selectedCountry, setSelectedCountry] = useState('');
  const [center, setCenter] = useState([0, 0]);
  const [zoom, setZoom] = useState(1);
  const [cont, setCountries] = useState("");

  useEffect(() => {
    async function fetchCountryData() {
      const res = await fetch("/CountriesView.json");
      const json = await res.json();
      setCountryDataJson(json);
    }

    fetchCountryData();
  }, []);

  useEffect(() => {
    const countries = countryDataJson && feature(countryDataJson, countryDataJson.objects.countries).features;

    if(countries) {
      const match = countries.find(x => x.properties.name.toLowerCase() === selectedCountry.trim().toLowerCase());

      countries.forEach(element => {
          console.log(element);
      });

      if(match) {
        console.log(match);
        setCenter(geoCentroid(match));
        setZoom(2);
      }
      else {
        setZoom(1);
        setCenter([0, 0]);
      }
    }
  }, [selectedCountry])

  return (
    countryDataJson && (
      <>
        <input value={selectedCountry} onChange={(e) => setSelectedCountry(e.target.value)} />
        <ComposableMap
          projection="geoMercator"
          className="map-container"
        >
          <ZoomableGroup
            zoom={zoom}
            center={center}
            minZoom={1}
            maxZoom={8}
            translateExtent={[[-1000, -500], [1000, 500]]}
          >
            <Geographies geography={countryDataJson}>
              {({ geographies }) =>
                geographies.map((geo) => {
                  const isSearched = geo.properties.name.toLowerCase() === selectedCountry.toLowerCase();
                  return (
                      <Geography
                        key={geo.rsmKey}
                        geography={geo}
                        fill={isSearched ? "#996cfb" : "#e0e0e0"}
                        stroke="#ffffff"
                        style={{
                          default: { outline: "none" },
                          hover: { fill: "#a78bfa", outline: "none" },
                          pressed: { fill: "#7243da", outline: "none" },
                        }}
                      />
                  )})
              }
            </Geographies>
          </ZoomableGroup>
        </ComposableMap>
        </>
    )
  );
}

export default WorldMap;
