import { useEffect, useState } from "react"
import GetCountriesFilter from "../../Controllers/Countries/GetCountriesFilter"


function FilterCountryDropdown({isVisible, filterCountryName}) {

    const [DropdownCountries, setDropDownCountries] = useState([]);


    useEffect(() => {
        const fetchCountries = async () => {

            if(filterCountryName !== '') {
                const countries = await GetCountriesFilter(filterCountryName);
                setDropDownCountries(countries);
            }
            else {
                setDropDownCountries([]);
            }
        };
      
        fetchCountries();
      }, [filterCountryName]);

      useEffect(() => {console.log(selectedCountries)}, [selectedCountries]);


    return (
        isVisible &&
            <div className="filter-dropdown">
                {selectedCountries && selectedCountries.map((country, index) => {
                return (
                    <div className="dropdown-country" key={index}>
                        <img src={country.additional_info.flag_path} className="dropdown-flag" />
                        {"\u00A0"}
                        {"\u00A0"}
                        <h5>{country.name}</h5>
                    </div>
                )})}
            </div>
    )
}

export default FilterCountryDropdown;
